style:
.card {
  background: #4B6A9B; /* deep blue */
  width: 100px;
  height: 100px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  transition: all 0.3s ease;
  border-radius: 10px;
  box-shadow: 0px 3px 15px rgba(0,0,0,0.2);
  color: white;
}

.card a {
  color: inherit;
  text-decoration: none;
  font-size: 14px;
}

.card a i {
  font-size: 24px;
  margin-bottom: 5px;
}

.card:hover {
  background: #3B5786; /* slightly darker blue */
  cursor: pointer;
  transform: translateY(-5px); /* slight upward movement */
  box-shadow: 0px 5px 20px rgba(0,0,0,0.3); /* deeper shadow */
}
